const { PrismaClient } = require(''@prisma/client'');
const prisma = new PrismaClient();

async function checkResults() {
  try {
    const audits = await prisma.audit.findMany({
      where: {
        url: {
          contains: ''livingspacearchitects''
        }
      },
      orderBy: {
        createdAt: ''desc''
      },
      take: 1,
      select: {
        id: true,
        url: true,
        results: true
      }
    });

    if (audits.length > 0) {
      const audit = audits[0];
      const results = typeof audit.results === ''string'' ? JSON.parse(audit.results) : audit.results;

      console.log(''Audit ID:'', audit.id);
      console.log(''URL:'', audit.url);
      console.log(''\nChecking for auditScope/auditType fields:'');
      console.log(''results.auditScope:'', results.auditScope);
      console.log(''results.auditType:'', results.auditType);
      console.log(''results.scope:'', results.scope);

      console.log(''\nTop level keys in results:'');
      console.log(Object.keys(results).slice(0, 20));
    } else {
      console.log(''No audits found'');
    }

    await prisma.$disconnect();
  } catch (error) {
    console.error(''Error:'', error);
    process.exit(1);
  }
}

checkResults();
")
